diagrams.code:
title Safety Evaluation Framework
autoNumber nested

// Actors declaration with icons and colors
Engineer      [icon: user,          color: green]
SafetyModel   [icon: shield,        color: blue]
TestEngine    [icon: cpu,           color: blue]
SUT           [icon: box,           color: red]
Analyzer      [icon: code,          color: yellow]
ReportGen     [icon: file-text,     color: yellow]
ReviewBoard   [icon: users,         color: green]

// Start evaluation request
Engineer      > TestEngine: Initiate evaluation request
activate TestEngine

// System initialization
TestEngine    > SUT: Initialize system under test
activate SUT
SUT           --> TestEngine: Initialization complete
deactivate SUT

// Run through each test case
loop [label: for each test case, icon: loop] {
  TestEngine  > SUT: Execute test case
  activate SUT
  SUT         --> TestEngine: Test result data
  deactivate SUT

  opt [label: detailed logging, icon: clipboard] {
    TestEngine -> DataStore: Log test result
  }

  break [label: critical hazard detected, icon: alert-triangle] {
    TestEngine --> Engineer: Abort evaluation due to critical hazard
  }
}

// Aggregate all results
TestEngine -> SafetyModel: Aggregate results
activate SafetyModel
SafetyModel --> TestEngine: Aggregation complete
deactivate SafetyModel

// Risk analysis
SafetyModel > Analyzer: Perform risk analysis
activate Analyzer
Analyzer    --> SafetyModel: Risk metrics
deactivate Analyzer

// Decision branch on risk level
alt [label: risk within acceptable limits, icon: check-circle] {
  Analyzer    -> ReportGen: Compile safety report
  activate ReportGen
  ReportGen   --> Analyzer: Report generated
  deactivate ReportGen

  Analyzer    --> Engineer: Deliver safety evaluation report
}
else [label: high risk identified, icon: x-circle] {
  Analyzer    -> ReviewBoard: Escalate high-risk issues
  activate ReviewBoard

  par [label: parallel review and remediation, icon: tasks] {
    and [label: board review] {
      ReviewBoard -> Engineer: Request mitigation plan
      Engineer    -> ReviewBoard: Submit mitigation plan
    }
    and [label: re-evaluation loop] {
      ReviewBoard -> TestEngine: Trigger focused re-tests
      activate TestEngine
      TestEngine  -> SUT: Re-run specified tests
      SUT         --> TestEngine: Revised test data
      deactivate TestEngine

      // Update aggregated results after re-tests
      TestEngine  -> SafetyModel: Update aggregate results
    }
  }

  deactivate ReviewBoard

  // Final reporting after remediation
  ReportGen -> Engineer: Provide updated safety report
}

deactivate TestEngine
