diagrams.code:
title Tool-Assisted Vulnerability Detection
autoNumber nested

// Actor definitions with icons and colors  
Security Analyst [icon: monitor, color: blue]  
"CI/CD Pipeline" [icon: cogs, color: green]  
Security Scanner [icon: shield, color: green]  
"Vulnerability DB" [icon: database, color: orange]  
Developer [icon: pencil, color: red]  
Notification Service [icon: mail, color: purple]  

// User triggers a scan  
Security Analyst > "CI/CD Pipeline": Start vulnerability scan  
activate Security Analyst  
"activate CI/CD Pipeline"  

// CI/CD invokes scanner  
"CI/CD Pipeline" > Security Scanner: Launch scan job  
activate Security Scanner  

loop [label: scan each code module, icon: repeat] {  
  Security Scanner -> "Vulnerability DB": Fetch CVE data  
  "Vulnerability DB" --> Security Scanner: Return vulnerability list  
  Security Scanner -> "CI/CD Pipeline": Intermediate findings  
}  

// Final report back to pipeline  
Security Scanner --> "CI/CD Pipeline": Complete scan report  
deactivate Security Scanner  

alt [label: vulnerabilities detected, icon: x] {  
  "CI/CD Pipeline" > Security Analyst: Send detailed report  
  activate Security Analyst  

  opt [label: require manual review, icon: flag] {  
    Security Analyst -> Security Analyst: Evaluate false positives  
  }  

  deactivate Security Analyst  

  par [label: notify stakeholders, icon: users] {  
    "CI/CD Pipeline" > Developer: Open issue in bug tracker  
  }  
  and [label: email alerts, icon: mail] {  
    "CI/CD Pipeline" -> Notification Service: Dispatch alert emails  
    Notification Service --> "CI/CD Pipeline": Delivery confirmation  
  }  
}  
else [label: no vulnerabilities, icon: check] {  
  "CI/CD Pipeline" --> Security Analyst: Scan passed with no issues  
}  

"deactivate CI/CD Pipeline"  
deactivate Security Analyst
